import React, { useEffect, useState } from "react";
// import { useForm } from "react-hook-form";
import { getProduct, getLocation, addToCard } from "./services/CalculatorS";
import { customDate, getNumberKey } from "utils/constants";
import Location from "./Location";
import "./Calculator.scss";

// requirement (do not need to do ADD LOCATION), so i'm assigned location with id = 4

const Calculator = () => {
  const [loading, setLoading] = useState(false);
  const [isSubmit, setIsSubmit] = useState(false);
  const [products, setProducts] = useState([]);
  const [locations, setLocations] = useState([]);
  const [params, setParams] = useState({
    date: null,
    product: ""
  });
  const [units, setunits] = useState(0);
  const [price, setPrice] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { date, product } = params;
    if (!date || !product) {
      alert("Please choose product or date");
      return;
    }
    const paramLocation = [
      {
        id: 4,
        quantity: units
      }
    ];
    const newParams = { ...params, locations: paramLocation };
    console.log("submit", newParams);
    try {
      setIsSubmit(true);
      const res = await addToCard(newParams);
      if (res && res.status === "success") {
        alert("Complete!");
      }
      console.log(res);
    } catch (err) {
      console.log(err);
    } finally {
      setIsSubmit(false);
    }
  };

  // Get date custom
  const { min, max } = customDate();

  const handleGetProduct = async () => {
    try {
      const res = await getProduct();
      console.log(res);
      setProducts(res);
    } catch (error) {
      console.log(error);
    } finally {
      console.log("finally");
    }
  };

  const handleGetLocation = async () => {
    try {
      setLoading(true);
      const res = await getLocation();
      console.log(res);
      setLocations(res);
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  const handleOnchange = (e) => {
    const { name, value } = e.target;
    const copiesParams = { ...params };
    if (name === "date") {
      if (!params.product) {
        alert("Please select product !");
        document.getElementById("date").value = null;
        resetDate();
        return;
      }
      const numberKey = getNumberKey(value);
      if (numberKey && numberKey > 0) {
        const objMaxPro = products.filter((p) => p.id === params.product)[0];
        const units = handleMaxPro(objMaxPro.max_production, numberKey);
        console.log(units);
        handleLocationUnit(units);
        setPrice(objMaxPro.price_per_unit);
      }
    }
    copiesParams[name] = value;
    setParams(copiesParams);
  };

  const handleMaxPro = (maxPro, key) => {
    if (!maxPro[key]) {
      const lastItem = Object.keys(maxPro)[Object.keys(maxPro).length - 1];
      return maxPro[lastItem];
    }
    return maxPro[key];
  };

  const handleLocationUnit = (units) => {
    const maxDist = locations[3].max_dist;
    console.log(maxDist, "max_dist");
    if (units > maxDist) {
      alert("The location units cannot be larger than the locations Max Distribution value");
      resetDate();
      return;
    }
    setunits(units);
  };

  const resetDate = () => (document.getElementById("date").value = null);

  useEffect(() => {
    handleGetProduct();
    handleGetLocation();
  }, []);

  if (loading) return <div>loading...</div>;

  return (
    <main className="main">
      <div className="cal-form">
        <form onSubmit={handleSubmit}>
          <div className="cal-form__field">
            <label>Product</label>
            <select name="product" onChange={handleOnchange}>
              <option value="">choose</option>
              {products.map((p) => {
                return (
                  <option key={p.id} value={p.id}>
                    {p.name}
                  </option>
                );
              })}
            </select>
          </div>
          <div className="cal-form__field">
            <label>Date</label>
            <input type="date" min={min} max={max} name="date" id="date" onChange={handleOnchange} />
          </div>
          <div className="cal-form__field">
            <Location place={locations[3]} units={units} cost={units * price} />
          </div>
          <div className="cal-form__field">
            <label>Total Units</label>
            <div>{units}</div>
          </div>
          <div className="cal-form__field">
            <label>Total Cost</label>
            <div>{units * price}</div>
          </div>

          {isSubmit ? (
            <div>Loading...</div>
          ) : (
            <button type="submit" className="btn">
              Submit
            </button>
          )}
        </form>
      </div>
    </main>
  );
};

export default Calculator;
